    def loadprojekt(self):
        con = sqlite3.connect('statika.db')
        cur = con.cursor()
        sql = 'select * from projectek'
        for row in cur.execute(sql):
            if (row[4] == '1'):
                self.Projektnev.setText(str(row[1]))

    def loadszelveny(self):
        con = sqlite3.connect('statika.db')
        cur = con.cursor()
        sql = 'select nev from szelveny order by nev'
        self.szelvenyek_listaja.addItem('')
        for row in cur.execute(sql):
            # print(str(row[0]))
            self.szelvenyek_listaja.addItem(str(row[0]))

    def loadadatok(self):
        con = sqlite3.connect('statika.db')
        cur = con.cursor()
        sql = 'select id,tartonev,hossz,konzol1,konzol2,szelveny from tartok '
        sql = sql + 'where projekt = (select projekt from projectek where aktiv = 1)'
        sorszam = 0
        for row in cur.execute(sql):
            # print(sql)
            rowPosition = self.tartolista.rowCount()
            self.tartolista.insertRow(rowPosition)
            self.tartolista.setItem(sorszam, 0, QtWidgets.QTableWidgetItem(str(row[0])))
            self.tartolista.setItem(sorszam, 1, QtWidgets.QTableWidgetItem(str(row[1])))
            self.tartolista.setItem(sorszam, 2, QtWidgets.QTableWidgetItem(str(row[2])))
            self.tartolista.setItem(sorszam, 3, QtWidgets.QTableWidgetItem(str(row[3])))
            self.tartolista.setItem(sorszam, 4, QtWidgets.QTableWidgetItem(str(row[4])))
            self.tartolista.setItem(sorszam, 5, QtWidgets.QTableWidgetItem(str(row[5])))
            sorszam = sorszam + 1

